import { Component, OnInit } from '@angular/core';
import { FoodService } from '../services/food/food.service';
import { Foods } from '../share/models/food';
// import { StarRatingComponent } from 'ng-starrating';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  foods:Foods[]= [];
  constructor(private fs:FoodService) { }

  ngOnInit(): void {
    this.foods = this.fs.getAll();   

  }

}
<ul>
    <li *ngFor="let food of foods ">
        <a>
        <img src="{{food.imageUrl}}" alt="food img">
        <div class="content">
            <div class="name">
                {{food.name}}
            </div>
            <span class="favorite {{food.favorite?'':'not'}}">â™¥</span>
            <!-- <star-rating 
                [value]="food.star" 
                [totalstars]="5" 
                checkedcolor="red" 
                uncheckedcolor="black" 
                size="24px" 
                [readonly]="true">
            </star-rating> -->

            <div class="product-item-footer">
                <div class="origins">
                    <span *ngFor="let origin of food.origins">
                        {{origin}}
                    </span>
                </div>
            </div>

            <div class="cook-timeime">
                <span>ðŸ•’</span>
                {{food.cookTime}}
            </div>

            <div class="price">
                {{food.price | currency :'&#8377;'}}
            </div>
        </div>
    </a>
    </li>
</ul>
